mapper.select:
select count(id) from orders

     and order_time >= #{begin}
     and order_time <= #{end}
     and status = #{status}

select *
from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select o.*, od.id od_id, od.name od_name, od.image, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor, od.number od_number, od.amount od_amount
from orders o left join order_detail od
on o.id = od.order_id

    and o.status = #{status}

        and user_id = #{userId}


order by order_time desc

select o.*, od.id od_id, od.name od_name, od.image, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor, od.number od_number, od.amount od_amount from orders o left join order_detail od on o.id = od.order_id
and o.id = #{id}
order by order_time desc

